import React, { useState } from "react";
import { FaRobot, FaUserMd } from "react-icons/fa";

const Check = () => {
  const [symptom, setSymptom] = useState("");
  const [chat, setChat] = useState([
    { sender: "bot", message: "üëã Hi! I'm your AI Health Assistant. Tell me your symptoms to get guidance." },
  ]);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (symptom.trim() === "") return;

    const updatedChat = [...chat, { sender: "user", message: symptom }];
    setChat(updatedChat);
    setSymptom("");
    setLoading(true);

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [
            { role: "system", content: "You are a health expert assistant providing health tips based on symptoms." },
            { role: "user", content: symptom },
          ],
          max_tokens: 200,
        }),
      });

      const data = await response.json();
      const aiResponse = data.choices[0]?.message?.content || "Sorry, I couldn't fetch a response. Try again!";

      setChat([...updatedChat, { sender: "bot", message: aiResponse }]);
    } catch (error) {
      console.error("Error fetching AI response:", error);
      setChat([...updatedChat, { sender: "bot", message: "‚ö†Ô∏è Something went wrong. Please try again later." }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5 p-4 rounded shadow-lg" style={{ background: "#f0f8ff" }}>
      <h2 className="text-center fw-bold mb-4" style={{ color: "#1976d2" }}>
        <FaRobot className="me-2" /> AI Symptom Chat
      </h2>

      <div className="chat-box p-3 bg-white rounded shadow mb-4" style={{ height: "400px", overflowY: "auto" }}>
        {chat.map((msg, index) => (
          <div
            key={index}
            className={`mb-3 p-3 rounded ${
              msg.sender === "bot" ? "bg-light text-dark" : "bg-primary text-white text-end"
            }`}
          >
            {msg.sender === "bot" ? <FaRobot className="me-2" /> : <FaUserMd className="me-2" />}
            {msg.message}
          </div>
        ))}
        {loading && <div className="text-muted">AI is typing...</div>}
      </div>

      <div className="input-group">
        <input
          type="text"
          className="form-control p-3"
          placeholder="Describe your symptom..."
          value={symptom}
          onChange={(e) => setSymptom(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button className="btn btn-info text-white fw-bold px-4" onClick={handleSend}>
          Send
        </button>
      </div>

      <div className="mt-4 p-4 bg-white rounded shadow text-center">
        <h5 className="fw-bold text-primary mb-3">‚ú® Benefits of Chat AI</h5>
        <ul className="list-unstyled text-muted fs-5">
          <li>‚úÖ Conversational AI for better experience</li>
          <li>‚úÖ Instant personalized symptom analysis</li>
          <li>‚úÖ AI-trained medical knowledge base</li>
          <li>‚úÖ Early detection and preventive tips</li>
          <li>‚úÖ Easy to use - just chat your problem</li>
        </ul>
      </div>
    </div>
  );
};

export default Check;
