import React, { useState } from "react";
import axios from "axios";
import { FaRobot, FaUserMd } from "react-icons/fa";

const Check = () => {
  const [symptom, setSymptom] = useState("");
  const [chat, setChat] = useState([
    { sender: "bot", message: "üëã Hi! I'm your AI Health Assistant. Tell me your symptoms to get guidance." },
  ]);

  // Access the API Key from .env (Ensure .env is at the project root)
  const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
  
  const handleSend = async () => {
    if (symptom.trim() === "") return;

    const updatedChat = [...chat, { sender: "user", message: symptom }];
    setChat(updatedChat);

    try {
      const response = await axios.post(
        "https://api.openai.com/v1/chat/completions",
        {
          model: "gpt-3.5-turbo",
          messages: [
            { role: "system", content: "You are a health assistant providing simple health tips based on user symptoms." },
            { role: "user", content: `I have ${symptom}. Suggest me health tips.` }
          ],
          max_tokens: 150,
          temperature: 0.7,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${apiKey}`,
          },
        }
      );

      const aiResponse = response.data.choices[0].message.content;
      setChat((prev) => [...prev, { sender: "bot", message: aiResponse }]);
    } catch (error) {
      console.error("AI API Error:", error);
      setChat((prev) => [...prev, { sender: "bot", message: "‚ö†Ô∏è Failed to fetch AI response. Please try again later." }]);
    }

    setSymptom("");
  };

  return (
    <div className="container mt-5 p-4 rounded shadow-lg bg-light">
      <h2 className="text-center fw-bold mb-4 text-primary">
        <FaRobot className="me-2" /> AI Symptom Chat
      </h2>

      <div className="chat-box p-3 bg-white rounded shadow mb-4" style={{ height: "400px", overflowY: "auto" }}>
        {chat.map((msg, index) => (
          <div
            key={index}
            className={`mb-3 p-3 rounded ${
              msg.sender === "bot" ? "bg-light text-dark" : "bg-primary text-white text-end"
            }`}
          >
            {msg.sender === "bot" ? <FaRobot className="me-2" /> : <FaUserMd className="me-2" />}
            {msg.message}
          </div>
        ))}
      </div>

      <div className="input-group">
        <input
          type="text"
          className="form-control p-3"
          placeholder="Describe your symptom..."
          value={symptom}
          onChange={(e) => setSymptom(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button className="btn btn-info text-white fw-bold px-4" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
};

export default Check;
