import React, { useState } from "react";

const Check = () => {
  const [symptom, setSymptom] = useState("");
  const [result, setResult] = useState("");

  const handleCheck = () => {
    // Dummy AI suggestion logic (you can replace this with your ML model/API)
    if (symptom.toLowerCase().includes("fever")) {
      setResult("You might have an infection or flu. Stay hydrated and consult a doctor.");
    } else if (symptom.toLowerCase().includes("headache")) {
      setResult("Possible causes: Stress, dehydration, or migraine. Try resting and drinking water.");
    } else if (symptom.toLowerCase().includes("cough")) {
      setResult("It might be a cold or allergy. Consider steam inhalation and consult if persistent.");
    } else if (symptom.trim() === "") {
      setResult("Please enter your symptom to get AI guidance.");
    } else {
      setResult("Symptom not recognized. Please consult a healthcare professional.");
    }
  };

  return (
    <div className="container mt-5 p-4 rounded shadow">
      <h2 className="text-center fw-bold" style={{ color: "#1976d2" }}>
        ðŸ¤– AI Symptom Checker
      </h2>
      <p className="text-center text-muted fs-5 mb-4">
        Get instant health insights powered by AI to guide you better!
      </p>

      <div className="mb-4">
        <label className="form-label fw-semibold">Enter Your Symptom:</label>
        <input
          type="text"
          className="form-control"
          placeholder="Eg: fever, headache, cough..."
          value={symptom}
          onChange={(e) => setSymptom(e.target.value)}
        />
      </div>

      <div className="text-center">
        <button className="btn btn-primary px-4" onClick={handleCheck}>
          Check
        </button>
      </div>

      {result && (
        <div className="alert alert-info mt-4 fw-semibold" role="alert">
          {result}
        </div>
      )}
    </div>
  );
};

export default Check;
