import React, { useState } from "react";
import { FaHeartbeat, FaStethoscope, FaRobot } from "react-icons/fa";

const Check = () => {
  const [symptom, setSymptom] = useState("");
  const [result, setResult] = useState("");

  const handleCheck = () => {
    if (symptom.toLowerCase().includes("fever")) {
      setResult("üî• You might have an infection or flu. Stay hydrated and consult a doctor.");
    } else if (symptom.toLowerCase().includes("headache")) {
      setResult("üíÜ Possible causes: Stress, dehydration, or migraine. Rest and stay hydrated.");
    } else if (symptom.toLowerCase().includes("cough")) {
      setResult("ü§ß Might be a cold or allergy. Consider steam inhalation and consult if persistent.");
    } else if (symptom.toLowerCase().includes("stomach pain")) {
      setResult("ü•¥ Could be indigestion, gastritis, or infection. Monitor and consult a doctor if severe.");
    } else if (symptom.trim() === "") {
      setResult("‚ö†Ô∏è Please enter your symptom to get AI guidance.");
    } else {
      setResult("‚ö†Ô∏è Symptom not recognized. Please consult a healthcare professional.");
    }
  };

  return (
    <div className="container mt-5 p-4 rounded shadow-lg" style={{ background: "#f5f9ff" }}>
      <h2 className="text-center fw-bold mb-4" style={{ color: "#1976d2" }}>
        <FaRobot className="me-2" /> AI Symptom Checker
      </h2>
      <p className="text-center text-muted fs-5 mb-4">
        <FaStethoscope className="me-2" /> Get instant health insights powered by AI to guide you better!
      </p>

      <div className="mb-4">
        <label className="form-label fw-semibold">Enter Your Symptom Below:</label>
        <input
          type="text"
          className="form-control p-3"
          placeholder="Eg: fever, headache, stomach pain..."
          value={symptom}
          onChange={(e) => setSymptom(e.target.value)}
        />
      </div>

      <div className="text-center">
        <button
          className="btn btn-info text-white px-5 py-2 fw-bold rounded-pill"
          onClick={handleCheck}
        >
          Analyze Now
        </button>
      </div>

      {result && (
        <div className="alert alert-primary mt-4 fw-semibold fs-5 shadow-sm" role="alert">
          {result}
        </div>
      )}

      {/* Benefits Section */}
      <div className="mt-5 p-4 bg-white rounded shadow">
        <h4 className="fw-bold text-center mb-3" style={{ color: "#1976d2" }}>
          <FaHeartbeat className="me-2" /> Why Use Our AI Checker?
        </h4>
        <ul className="list-unstyled text-muted fs-5">
          <li>‚úÖ Instant health analysis powered by AI</li>
          <li>‚úÖ Get personalized insights within seconds</li>
          <li>‚úÖ Early detection of common symptoms</li>
          <li>‚úÖ User-friendly and responsive design</li>
          <li>‚úÖ Completely free to use anytime</li>
        </ul>
      </div>
    </div>
  );
};

export default Check;
