import React from "react";
import { Link } from "react-router-dom";
import homeImage from "../assests/home-image.avif";
import "bootstrap/dist/css/bootstrap.min.css";

const Home = () => {
  return (
    <div
      className="container mt-5 py-5"
      style={{
        background: "linear-gradient(to right, #f8f9fa, #e3f2fd)",
        borderRadius: "15px",
      }}
    >
      <div className="row align-items-center">
        {/* Left Side Content */}
        <div className="col-md-6 mb-4">
          <h1 style={{ fontWeight: "700", color: "#1976d2" }}>
            Welcome to <span style={{ color: "#00bcd4" }}>NutriFit-Hub</span>
          </h1>
          <p className="lead" style={{ color: "#555", lineHeight: "1.8" }}>
            Fuel your fitness journey with personalized <span style={{ color: "#1976d2", fontWeight: "600" }}>health tips</span>,
            expert-crafted <span style={{ color: "#1976d2", fontWeight: "600" }}>diet plans</span>, and easy-to-follow
            <span style={{ color: "#1976d2", fontWeight: "600" }}> fitness routines</span>. Start your transformation today!
          </p>
          <div className="mt-4">
            <Link to="/dashboard" className="btn btn-primary me-3 px-4 py-2">
              Get Started
            </Link>
            <Link to="/about" className="btn btn-outline-secondary px-4 py-2">
              Learn More
            </Link>
          </div>
        </div>

        {/* Right Side Image */}
        <div className="col-md-6 text-center">
          <img
            src={homeImage}
            alt="Healthy Lifestyle"
            className="img-fluid rounded shadow"
            style={{
              maxHeight: "400px",
              width: "100%",
              objectFit: "cover",
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default Home;
